query.Projection.Add("Name");
query.Projection.Add("Price");

using (IDataReader reader = unitOfWork.Project(query))
{
  while (reader.Read())
  {
    yield return new { Name = reader.GetString(0), Price = reader.GetDecimal(1) };
  }
}

